$(document).ready(function(){}),$(window).resize(function(){});

var slider = $('.slider');

slider.on("init", function(event, slick){
    $(".slider_count").html(parseInt(slick.currentSlide + 1) + ' / <span>' + slick.slideCount + '</span>');
});

slider.on("afterChange", function(event, slick, currentSlide){
    $(".slider_count").html(parseInt(slick.currentSlide + 1) + ' / <span>' + slick.slideCount + '</span>');
});

slider.slick({
    slidesToShow: 4,
    slidesToScroll: 1,
    nextArrow: '<button class="slick-arrow next"><img src="../img/slider-next.png"></button>',
    prevArrow: '<button class="slick-arrow prev"><img src="../img/slider-prev.png"></button>',
    responsive: [
        {
          breakpoint: 1240,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 1,
          }
        },
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 1,
          }
        },
        {
          breakpoint: 768,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          }
        }
    ]
});

$('.item .text h5').matchHeight({
  byRow: true
});

$('.item .text p').matchHeight({
  byRow: true
});