
$('.fagTabs').responsiveTabs({
    duration: 200,
    startCollapsed: 'accordion',
    animation: 'slide',
});

$(".bottom-price a").on("click", function (event) {
    event.preventDefault();
    var id  = $(this).attr('href'),
        top = $(id).offset().top - 80;
    $('body,html').animate({scrollTop: top}, 800,);
});



$('.cloze').on('click', function(){
    $('.popup-img-reestr').removeClass('active');
});

$('.cloze-popup').on('click', function(){
    $('.popup-img-reestr').removeClass('active');
});

$('.link-popup').on('click', function(){
    $('.popup-img-reestr').addClass('active');
});

var popupSlider = $('.popup-slider');

popupSlider.on("init", function(event, slick){
    $(".popup_count").html(parseInt(slick.currentSlide + 1) + ' / <span>' + slick.slideCount + '</span>');
});

popupSlider.on("afterChange", function(event, slick, currentSlide){
    $(".popup_count").html(parseInt(slick.currentSlide + 1) + ' / <span>' + slick.slideCount + '</span>');
});

popupSlider.slick({
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: '<button class="slick-arrow next"><img src="../img/slider-next.svg"></button>',
    prevArrow: '<button class="slick-arrow prev"><img src="../img/slider-prev.svg"></button>',
});

$(document).keyup(function(e) { 
    
    if(e.which == 27){
        $('.popup-img-reestr').removeClass('active');
    }

});

